import React from 'react';
import InfosClient from './InfosClient';
import Transactions from './Transactions';
import client from "../metier/client";
import Transaction from "../metier/Transaction";
import Dbal from "../modele/dbal";

export default class Page extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            client: props.client,
        }
    }

    render() {
        return (
            <section className="credits">
                <h2 className="title">CREDITS</h2>
                <div className="saisie">
                    <legend className="description">TYPE</legend>
                    <select className="zone type">
                        <option value="Carte bancaire">Carte bancaire</option>
                        <option value="Cheque">Cheque</option>
                    </select>
                </div>
                <div className="saisie">
                    <legend className="description">MONTANT</legend>
                    <input type="number" className="zone montant"/>
                </div>
                <div className="saisie">
                    <legend className="description">NUMERO</legend>
                    <input className="zone numero"/>
                </div>
                <div className="saisie">
                    <legend className="description">COMMENTAIRE</legend>
                    <textarea className="zone commentaire"/>
                </div>
                <section className="actions">
                    <button className="ajouter">AJOUTER</button>
                </section>
            </section>
        )
    }
}
