import React from 'react';
import DataList from "./DataList";
import client from "../metier/client";

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            listeClients: [],
            client: new client(null),
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        listeClients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    change(propriete, event){
        const client = this.state.client;
        client[propriete] = event.target.value;
        this.setState({
            client: client
        })
    }

    button(nom){
        return(
            <button onClick={this.change.bind(this, nom)}>nom</button>
        )
    }

    selectClient(infos){
        this.setState({
            client: new client(infos)
        })
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            //
            const client = this.state.client;
            return (
                <section className="page">
                    <section className="entete">
                        <section className="navigation">
                            <h2 className="title">Comptabilité</h2>
                            <h3 className="nav">Client</h3>
                            <h3 className="nav">Transactions</h3>
                            <h3 className="nav">Crédits</h3>
                        </section>
                        <section className="stickClient">
                            <h3>Nom</h3>
                            <h3>Prenom</h3>
                            <h3>Mail</h3>
                        </section>
                    </section>
                    <section className="corps">
                        <section className="client">
                            <h2 className="title">CLIENTS</h2>
                            <div className="saisie">
                                <legend className="description">NOM</legend>
                                <input className="zone prenom" type="text" list="listeClient"/>
                                <datalist id="listeClient"></datalist>
                            </div>
                            <div className="saisie">
                                <legend className="description">PRENOM</legend>
                                <input className="zone prenom" type="text"/>
                            </div>
                            <div className="saisie">
                                <legend className="description">VILLE</legend>
                                <input className="zone ville" type="text"/>
                            </div>
                            <div className="saisie">
                                <legend className="description">E-MAIL</legend>
                                <input className="zone mail" type="text"/>
                            </div>
                            <div className="saisie">
                                <legend className="description">TELEPHONE</legend>
                                <input className="zone telephone" type="text"/>
                            </div>
                            <div className="saisie">
                                <legend className="description">SOLDE</legend>
                                <input className="zone solde" type="text" disabled/>
                            </div>
                            <section className="actions">
                                <button className="editer">MODIFIER</button>
                                <button className="archiver">ARCHIVER</button>
                            </section>
                        </section>
                        <section className="transaction">
                            <h2 className="title">TRANSACTIONS</h2>
                            <section className="tableau">
                                <h3 className="colonne">Date</h3>
                                <h3 className="colonne bordure">Montant</h3>
                                <section className="ligne">
                                    <span className="date">23/09/2020</span>
                                    <span className="montant bordure">15€</span>
                                </section>
                                <section className="ligne">
                                    <span className="date">23/09/2020</span>
                                    <span className="montant bordure">15€</span>
                                </section>
                                <section className="ligne">
                                    <span className="date">23/09/2020</span>
                                    <span className="montant bordure">15€</span>
                                </section>
                            </section>
                        </section>
                    </section>
                </section>
            )
        }
    }
}

export default InfosClient;
