import React from 'react';
import client from "../metier/client";

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            update: 0,
            error: null,
            listeClients: [],
            client: new client(null),
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        listeClients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    update(){
        this.setState({
            update: this.state.update + 1
        })
    }

    change(propriete, event){
        const client = this.state.client;
        client[propriete] = event.target.value;
        this.setState({
            client: client
        })
    }

     selectClient(event){
        if (event.nativeEvent.data === undefined){
            const options =  Array.from(event.target.list.children);
            options.forEach((option) => {
                if (option.value === event.target.value){
                    let clientSelect = JSON.parse(option.dataset.client);
                    clientSelect = new client(clientSelect);
                    console.log(clientSelect);
                    setTimeout(()=>{
                        this.setState({
                            client: clientSelect
                        })
                    },700);
                }
            })
        }
        else{
            const client = this.state.client;
            client.nom = event.target.value;
            this.setState({
                client: client
            })
        }
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            const client = this.state.client;
            //console.log(client);
            return (
                <section className="selectionClient">
                    <section className="client">
                        <h2 className="title">CLIENTS</h2>
                        <div className="saisie">
                            <legend className="description">NOM</legend>
                            <input className="zone prenom"  type="text" list="listeClient" value={client.nom} onChange={this.selectClient.bind(this)}/>
                            <datalist id="listeClient">
                                {this.state.listeClients.map((client, index) => {
                                    return (
                                        <option key={index}  data-client={JSON.stringify(client)}>{client.nom + client.prenom}</option>
                                    )
                                })}
                            </datalist>
                        </div>
                        <div className="saisie">
                            <legend className="description">PRENOM</legend>
                            <input className="zone prenom" type="text" value={client.prenom} onChange={this.change.bind(this, "prenom")}/>
                        </div>
                        <div className="saisie">
                            <legend className="description">VILLE</legend>
                            <input className="zone ville" type="text" value={client.ville} onChange={this.change.bind(this, "ville")}/>
                        </div>
                        <div className="saisie">
                            <legend className="description">E-MAIL</legend>
                            <input className="zone mail" type="text" value={client.mail} onChange={this.change.bind(this, "mail")}/>
                        </div>
                        <div className="saisie">
                            <legend className="description">TELEPHONE</legend>
                            <input className="zone telephone" type="text" value={client.tel} onChange={this.change.bind(this, "tel")}/>
                        </div>
                        <div className="saisie">
                            <legend className="description">SOLDE</legend>
                            <input className="zone solde" type="text" value={(client.solde=== "")?this.update():""} disabled/>
                        </div>
                        <section className="actions">
                            <button className="editer">MODIFIER</button>
                            <button className="archiver">ARCHIVER</button>
                        </section>
                    </section>
                    <section className="transaction">
                        <h2 className="title">TRANSACTIONS</h2>
                        <section className="tableau">
                            <h3 className="colonne">Date</h3>
                            <h3 className="colonne bordure">Montant</h3>
                            <section className="ligne">
                                <span className="date">23/09/2020</span>
                                <span className="montant bordure">15€</span>
                            </section>
                            <section className="ligne">
                                <span className="date">23/09/2020</span>
                                <span className="montant bordure">15€</span>
                            </section>
                            <section className="ligne">
                                <span className="date">23/09/2020</span>
                                <span className="montant bordure">15€</span>
                            </section>
                        </section>
                    </section>
                </section>
            )
        }
    }
}

export default InfosClient;
