import React from  'react'

class DataList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: props.options,
            filtre: [],
            textValue:''
        };
    }

    updateFilter(evt) {

        let value = '';

        if (evt) {
            value = evt.target.value;
        }

        let filteredOptions = [];

        for (let i = 0;i<this.props.options.length;i++) { // this is the idea of creating div//
            const item = this.props.options[i];
            if (item.value.indexOf(value) !== -1) {
                filteredOptions.push({
                    id: item.id,
                    value: item.value
                });
            }
        }

        this.setState({
            filteredOptions: filteredOptions,
            textValue: value,
        });
    }

    render() {
        let displayList = this.state.filteredOptions.map((option) => {
            return (<div data-id={option.id} onClick={()=>this.handleClick(option)}>{option.value}</div>)
        });
        return(

            <div>
                <input type="text" value={this.state.textValue}  onChange={this.filtre.bind(this)} onFocus={this.showList}/>
                <div>
                    {
                        this.state.expanded && displayList
                    }
                </div>

            </div>
        )
    }
}

export default DataList;
