import React from 'react';

export default class DetailsTransaction extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            reservationDetailee: null,
        }
    }

    async componentDidMount() {
        let resaDetail = this.props.reservationDetailee;
        await resaDetail.then((resolve)=>{
            resaDetail = resolve;
        });
        this.setState({
            initialized: true,
            reservationDetailee: resaDetail,
        });
    }

    render() {
        if (!this.state.initialized) {return null}
        else{
            const rd = this.state.reservationDetailee;
            const options = {hour: "numeric", day: "numeric", month: "short", year: "numeric"};
            console.log(rd);
            return (
                <section className="details">
                    <span className="salle">{"Salle n°" + rd[1].numero}</span>
                    <span className="theme">{"Theme : " + rd[2].nom}</span>
                    <span className="nbJoueur">{"Nombre de joueur : " + rd[1].nbJoueur}</span>
                    <span className="heureArrivee">{"Heure d'arrivée : " + rd[1].numero}</span>
                </section>
            )
        }
    }
}
