class client{
    constructor(infos) {
        this.id = (infos)?infos.id:"";
        this.nom = (infos)?infos.nom:"";
        this.prenom = (infos)?infos.prenom:"";
        this.ville = (infos)?this.getVille(infos.ville):"";
        this.tel = (infos)?infos.tel:"";
        this.mail = (infos)?infos.mail:"";
        this.archive = (infos)?infos.archive:"";
    }

    getVille(path){
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        listeClients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }
}

export default client;
