import React from 'react';

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            clients: [],
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        clients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    change(nom){
        console.log(nom);
    }

    button(nom){
        return(
            <button onClick={this.change.bind(this, nom)}>nom</button>
        )
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            return (
                <section className="client">
                    <script src="../src/includes/jquery.js"></script>
                    <input type="text" list="clientsList"/>
                    <datalist id="clientsList">
                        {this.state.clients.map((client, key) => {
                            return (
                                <button key={key} value={this.button(client.nom)}/>
                            )
                        })}
                    </datalist>
                </section>
            )
        }
    }
}

$(document).on('change', 'input', function(){
    var optionslist = $('datalist')[0].options;
    var value = $(this).val();
    for (var x=0;x<optionslist.length;x++){
        if (optionslist[x].value === value) {
            //Alert here value
            alert(value);
            break;
        }
    }
});

export default InfosClient;
