import React from 'react';
import DataList from "./DataList";
import client from "../metier/client";

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            listeClients: [],
            client: new client(null),
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        listeClients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    change(propriete, event){
        const client = this.state.client;
        client[propriete] = event.target.value;
        this.setState({
            client: client
        })
    }

    button(nom){
        return(
            <button onClick={this.change.bind(this, nom)}>nom</button>
        )
    }

    selectClient(infos){
        this.setState({
            client: new client(infos)
        })
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            const client = this.state.client;
            console.log(client);
            return (
                <section className="client">
                    <DataList options={this.state.listeClients} onSelectOption={this.selectClient.bind(this)}/>
                    <input type="text" value={(client)?client.prenom:""} onChange={this.change.bind(this, "prenom")}/>
                </section>
            )
        }
    }
}

export default InfosClient;
