import Dbal from "../modele/dbal";

class Obstacle{
    constructor(infos) {
        this.id = (infos)?infos.id:"";
        this.position = (infos)?infos.position:"";
        this.commentaire = (infos)?infos.commentaire:"";
        this.qte = (infos)?infos.qte:0;
        this.article_id = (infos)?infos.article_id:" ";
        this.reservation_id = (infos)?infos.reservation_id:" ";
        this.nom = (infos)?this.loadNom(infos.article_id):" ";
    }

    async loadNom(id){
        const d = new Dbal();
        const response = await d.get("http://localhost:8080/api/article/get/" + id);
        this.nom = JSON.parse(response);
    }
}

export default Obstacle;
