class client{
    constructor(infos) {
        this.id = (infos)?infos.id:"";
        this.nom = (infos)?infos.nom:"";
        this.prenom = (infos)?infos.prenom:"";
        this.ville = (infos)?infos.ville:"";
        this.tel = (infos)?infos.tel:"";
        this.mail = (infos)?infos.mail:"";
        this.solde = 0;
        this.archive = (infos)?infos.archive:"";

        this.getVille();
        this.getSolde();
    }

    getVille(){
        if (this.ville !== ""){
            const self = this;
            fetch("http://localhost:8080" + this.ville)
                .then(res => res.json())
                .then(
                    (result) => {
                        self.ville = result.nom;
                    },
                    (error) => {
                        console.log(error);
                    }
                )
        }
    }

    getSolde(){
        const self = this;
        fetch("http://localhost:8080/client/soldeById/"+this.id)
            .then(res => res.json())
            .then(
                (result) => {
                    self.ville = result.nom;
                },
                (error) => {
                    console.log(error);
                }
            )
    }
}

export default client;
