import Dbal from "../modele/dbal";
import client from "../metier/client";

export default class DaoClient {
    constructor() {
        if (!!DaoClient.instance) {
            return DaoClient.instance;
        }
        DaoClient.instance = this;
        this.dbal = new Dbal();
        return this;
    }

    async getAll(){
        const liste =  [];
        const reponse = await this.dbal.get("clients");
        const clients = JSON.parse(reponse);
        for (const c of clients) {
            liste.push(new client(c));
        }
        return liste;
    }

    async load(client){
        let response = await this.dbal.get(client.ville);
        client.ville = (JSON.parse(response)).nom;
        response = await d.get("http://localhost:8080/api/client/soldeById/" + client.id);
        client.solde = JSON.parse(response);
        return new client(client);
    }
}
