import Dbal from "../modele/dbal";
import client from "../metier/client";

export default class DaoClient {
    constructor() {
        if (!!DaoClient.instance) {
            return DaoClient.instance;
        }
        DaoClient.instance = this;
        this.dbal = new Dbal();
        return this;
    }

    async getAll(){
        const liste =  [];
        const reponse = await this.dbal.get("/api/clients");
        const clients = JSON.parse(reponse);
        for (const c of clients) {
            liste.push(new client(c));
        }
        return liste;
    }

    async load(c){
        let response = await this.dbal.get(c.ville);
        c.ville = (JSON.parse(response)).nom;
        response = await this.dbal.get("/api/client/soldeById/" + c.id);
        c.solde = JSON.parse(response);
        return new client(c);
    }
}
