import React from 'react';
import DataList from "./DataList";
import client from "../metier/client"

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            clients: [],
            clientSelect: null,
            clientFinal: new client(null)
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        clients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    change(nom){
        console.log(nom);
    }

    button(nom){
        return(
            <button onClick={this.change.bind(this, nom)}>nom</button>
        )
    }

    selectClient(client){
        this.setState({
            clientSelect: client
        })
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            const client = this.state.clientSelect;
            return (
                <section className="client">
                    <DataList options={this.state.clients} onSelectOption={this.selectClient.bind(this)}/>
                    <input type="text" value={(client !== null)?client.prenom:""}/>
                </section>
            )
        }
    }
}

export default InfosClient;
