import React from  'react'

class DataList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: props.options,
            filtre: [],
            textValue:''
        };
    }

    render() {
        let displayList = this.state.filteredOptions.map((option) => {
            return (<div data-id={option.id} onClick={()=>this.handleClick(option)}>{option.value}</div>)
        });
        return(

            <div>
                <input type="text" value={this.state.textValue}  onChange={this.filtre.bind(this)} onFocus={this.showList}/>
                <div>
                    {
                        this.state.expanded && displayList
                    }
                </div>

            </div>
        )
    }
}

export default DataList;
