import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import client from "./metier/client";

const clients = [];
 function loadClient(){
      fetch("http://localhost:8080/api/clients")
        .then(res => res.json())
        .then(
            async (result) => {
                const create = result.map((c)=> {
                    clients.push(new client(c));
                });
                console.log(result);
                return await Promise.all(create);
            },
            (error) => {console.log(error);}
        );
}

async function main() {
    try {
        await loadClient();
        console.log("OK");
    } catch (e) {
        console.log("FAIL");
    }
}

ReactDOM.render(
    main(),
    <App clients={clients}/>,
    document.getElementById('page_backend'),
);
