import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import client from "./metier/client";

function loadClient(){
    const clients = [];
     fetch("http://localhost:8080/api/clients")
        .then(res => res.json())
        .then(
            async (result) => {
                const create = result.forEach(function (c) {
                    clients.push(new client(c));
                });
                return await Promise.all(create);
            },
            (error) => {console.log(error);}
        );
}

ReactDOM.render(
    <App clients={async()=>{loadClient()}}/>, document.getElementById('page_backend'),
);
