import React from  'react'

class DataList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: props.options,
            filtre: [],
            textValue:''
        };
    }

    changerFiltre(event){
        const result = [];
        this.state.options.forEach(function (client) {
            if (client.nom.include(event.target.value)){
                result.push(client);
            }
        });
        console.log(result);
        this.setState({
            textvalue: event.target.value
        })
    }

    render() {
        console.log(this.state.filtre);
        return(
            <section className="DataList">
                <input type="text" value={this.state.textValue}  onChange={this.changerFiltre.bind(this)} onFocus={this.showList}/>
            </section>
        )
    }
}

export default DataList;
