class client{
    constructor(infos) {
        this.id = (infos)?infos.id:"";
        this.nom = (infos)?infos.nom:"";
        this.prenom = (infos)?infos.prenom:"";
        this.ville = (infos)?this.getVille(infos.ville):"";
        this.tel = (infos)?infos.tel:"";
        this.mail = (infos)?infos.mail:"";
        this.solde = this.getSolde();
        this.archive = (infos)?infos.archive:"";
    }

    getVille(path){
        const self = this;
        fetch("http://localhost:8080" + path)
            .then(res => res.json())
            .then(
                (result) => {
                    self.ville = result.nom;
                },
                (error) => {
                    console.log(error);
                }
            )
    }

    getSolde(){
        const self = this;
        fetch("http://localhost:8080")
            .then(res => res.json())
            .then(
                (result) => {
                    self.ville = result.nom;
                },
                (error) => {
                    console.log(error);
                }
            )
    }
}

export default client;
