class client{
    constructor(infos) {
        this.id = (infos)?infos.id:"";
        this.nom = (infos)?infos.nom:"";
        this.prenom = (infos)?infos.prenom:"";
        this.ville = (infos)?infos.prenom:"";
        this.tel = (infos)?infos.tel:"";
        this.mail = (infos)?infos.mail:"";
        this.solde = (infos)?this.getSolde():0;
        this.archive = (infos)?infos.archive:"";
    }

       getVille(path){
        if (this.ville !== "") {
            const self = this;
            fetch("http://localhost:8080" + path)
                .then(async res => await res.json())
                .then(
                    (result) => {
                        self.ville =  result.nom;
                    },
                    (error) => {
                        console.log(error);
                        self.ville = "";
                    }
                )
        }
    }

    getSolde(){
        if (this.id !== ""){
            const self = this;
            fetch("http://localhost:8080/api/client/soldeById/"+this.id)
                .then(res => res.json())
                .then(
                    (result) => {
                        self.solde =  result[0][1];
                    },
                    (error) => {
                        console.log(error);
                        self.solde = 0;
                    }
                )
        }
    }
}

export default client;
