import React from 'react';

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            clients: [],
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        clients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
