import React from  'react'

class DataList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: props.options,
            filtre: [],
            textValue:'',
            display: 'none'
        };
    }

    changerFiltre(event){
        const recherche = event.target.value.toString().toUpperCase();
        this.setState({
            filtre: this.state.options.filter(client => client.nom.includes(recherche) || client.prenom.includes(recherche)),
            textValue: event.target.value
        })
    }

    afficherOptions(){
        this.setState({
            display: 'block'
        })
    }

    cacherOptions(){

        this.setState({
            display: 'none'
        })
    }

    selectOption(option){
        this.setState({
           textValue: option.nom + " " + option.prenom
        });
        this.cacherOptions();
    }

    render() {
        const display = this.state.display;
        return(
            <section className="DataList">
                <input type="text" value={this.state.textValue}  onChange={this.changerFiltre.bind(this)} onFocus={this.afficherOptions.bind(this)}/>
                {this.state.filtre.map((option, i) =>{
                    return(
                        <div style={{display: display}} key={i} onClick={this.selectOption.bind(this, option)}>{option.nom + " " + option.prenom}</div>
                    )
                })}
            </section>
        )
    }
}

export default DataList;
