import React from 'react';

class InfosClient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            initialized: false,
            error: null,
            clients: [],
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/api/clients")
            .then(res => res.json())
            .then(
                (result) => {
                    const clients = [];
                    result.forEach(function (client) {
                        clients.push(client);
                    });
                    this.setState({
                        initialized: true,
                        clients: clients
                    });
                },
                (error) => {
                    this.setState({
                        initialized: true,
                        error: error
                    });
                }
            )
    }

    change(){
        console.log("ici");
    }

    render() {
        if(this.state.initialized === false){return null}
        else {
            return (
                <section className="client">
                    <input type="text" list="clientsList"/>
                    <datalist id="clientsList" onChange={this.change.bind(this)}>
                        {this.state.clients.map((client, key) => {
                            return (
                                <option key={key} value={client.nom}/>
                            )
                        })}
                    </datalist>
                </section>
            )
        }
    }
}

export default InfosClient;
